mapper.update:
update business_user
customer_num = #{customerNum}, province = #{province}, membership = #{membership}, product_id = #{productId}, product_name = #{productName}, belong_cp = #{belongCp}, develope_num = #{developeNum}, develope_person = #{developePerson}, order_time = #{orderTime}, call_time = #{callTime}, product_assets = #{productAssets}, product_rate = #{productRate},
where id = #{id}

mapper.select:
select membership from business_user
where customer_num = #{customerNum}
and product_id= #{productId}
and order_time= #{orderTime}
